INSERT INTO plant_families_detailed (scientific_name, general_description, environment, leaf_type, root_type, flower_description, toxicity, common_genera, notable_species, care_light, care_watering, care_temperature, care_soil, common_pests, common_diseases) VALUES ('Primulaceae', 'Малко семейство от тревисти растения с декоративни цветове.', 'Умерени и планински области на северното полукълбо', 'Прости, яйцевидни листа', 'Стандартна влакнеста коренова система', 'Цветове с пет венчелистчета, често ярки и симетрични', 'Повечето видове не са токсични', 'Primula, Lysimachia, Anagallis', 'Primula vulgaris, Primula veris', 'Светло до полусянка', 'Умерено, без да се преовлажнява', '10-20°C', 'Добре дренирана, богата на хумус почва', 'Охлюви, листни въшки', 'Кореново гниене, брашнеста мана');
INSERT INTO plant_families_detailed (scientific_name, general_description, environment, leaf_type, root_type, flower_description, toxicity, common_genera, notable_species, care_light, care_watering, care_temperature, care_soil, common_pests, common_diseases) VALUES ('Asparagaceae', 'Голямо семейство с тревисти и дървовидни растения, често със сочни стъбла.', 'Тропически и умерени зони', 'Линейни или лентовидни листа', 'Добре развита коренова система, често с грудки', 'Малки цветове в гроздове или метлици', 'Нетоксични до слабо токсични видове', 'Asparagus, Agave, Yucca', 'Asparagus officinalis, Yucca filamentosa', 'Ярка светлина до полусянка', 'Умерено поливане, без задържане на вода', '15-25°C', 'Добре дренирана, песъчлива почва', 'Паяжинообразни акари, щитоносни въшки', 'Кореново гниене, листни петна');
INSERT INTO plant_families_detailed (scientific_name, general_description, environment, leaf_type, root_type, flower_description, toxicity, common_genera, notable_species, care_light, care_watering, care_temperature, care_soil, common_pests, common_diseases) VALUES ('Marantaceae', 'Тропическо семейство с декоративни листа и движение на листата през деня.', 'Влажни тропически гори', 'Широки, шарени, често с червени жилки', 'Плитка и разклонена коренова система', 'Малки, не особено ефектни цветове', 'Нетоксични', 'Maranta, Calathea, Ctenanthe', 'Maranta leuconeura, Calathea orbifolia', 'Дифузна светлина без пряко слънце', 'Постоянна влага, без засушаване', '18-26°C', 'Лека, влажна, добре дренирана почва', 'Трипси, листни въшки', 'Петна по листата, бактериални инфекции');
INSERT INTO plant_families_detailed (scientific_name, general_description, environment, leaf_type, root_type, flower_description, toxicity, common_genera, notable_species, care_light, care_watering, care_temperature, care_soil, common_pests, common_diseases) VALUES ('Orchidaceae', 'Едно от най-големите семейства с екзотични цветове и епифитен растеж.', 'Тропици и субтропици, някои видове и в умерения пояс', 'Месести, понякога восъчни', 'Корени с веламен, адаптирани за въздушна влага', 'Комплексни, симетрични, цветни', 'Някои видове са слабо токсични', 'Phalaenopsis, Dendrobium, Cattleya', 'Phalaenopsis amabilis, Dendrobium nobile', 'Светло, но без пряко слънце', 'След като почвата изсъхне напълно', '18-28°C', 'Специален орхидеен субстрат', 'Щитоносни въшки, акари', 'Гниене на корени, гъбични инфекции');
